import React from 'react';
import { ChevronDown, Star, Heart, Shield } from 'lucide-react';
import { RESORT_INFO } from '../utils/constants';
import { scrollToSection } from '../utils/scrollUtils';

const Hero = () => {
  const scrollToNext = () => {
    scrollToSection('#servicios');
  };

  const scrollToContact = () => {
    scrollToSection('#contacto');
  };

  const scrollToGallery = () => {
    scrollToSection('#galeria');
  };

  return (
    <section id="inicio" className="relative h-screen flex items-center justify-center overflow-hidden bg-kawai-sand">
      <div className="absolute inset-0 z-0">
        {/* Video de fondo usando iframe de YouTube */}
        <iframe
          src="https://www.youtube.com/embed/o36HKsDKGAU?autoplay=1&mute=1&loop=1&playlist=o36HKsDKGAU&controls=0&showinfo=0&rel=0&modestbranding=1&playsinline=1"
          title="Kawai Resort Video"
          className="w-full h-full object-cover"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        />
        <div className="absolute inset-0 bg-gradient-to-b from-black/50 via-black/30 to-black/60"></div>
      </div>

      <div className="relative z-10 text-center text-white px-4 sm:px-6 lg:px-8 max-w-5xl mx-auto">
        {/* Icono decorativo */}
        <div className="inline-flex items-center justify-center w-16 h-16 sm:w-20 sm:h-20 bg-white/10 backdrop-blur-sm rounded-full mb-6 sm:mb-8 animate-fade-in">
          <Star className="w-8 h-8 sm:w-10 sm:h-10 text-kawai-sun" />
        </div>

        {/* Título principal */}
        <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl xl:text-7xl font-lato font-black mb-4 sm:mb-6 animate-fade-in leading-tight">
          Bienvenidos a
          <span className="block text-kawai-sun mt-2 sm:mt-3">Kawai Resort</span>
        </h1>

        {/* Tagline */}
        <p className="text-base sm:text-lg md:text-xl lg:text-2xl mb-6 sm:mb-8 max-w-3xl mx-auto animate-slide-up font-light leading-relaxed px-2">
          Tu destino perfecto para vacaciones familiares junto al mar. 
          Creando momentos inolvidables desde 1995.
        </p>

        {/* Botones principales */}
        <div className="flex flex-col sm:flex-row gap-3 sm:gap-4 justify-center items-center animate-slide-up mb-8 sm:mb-12">
          <button 
            onClick={scrollToContact}
            className="w-full sm:w-auto bg-gradient-to-r from-kawai-coral to-kawai-sun text-white text-base sm:text-lg px-6 sm:px-8 py-3 sm:py-4 rounded-xl font-bold transition-all duration-300 transform hover:scale-105 hover:shadow-lg shadow-md"
          >
            🏖️ Reservar Ahora
          </button>
          <button 
            onClick={scrollToGallery}
            className="w-full sm:w-auto bg-white/10 backdrop-blur-sm border-2 border-white text-white text-base sm:text-lg px-6 sm:px-8 py-3 sm:py-4 rounded-xl font-bold hover:bg-white hover:text-kawai-deep transition-all duration-300 transform hover:scale-105"
          >
            📸 Ver Galería
          </button>
        </div>

        {/* Estadísticas mejoradas */}
        <div className="grid grid-cols-3 gap-4 sm:gap-6 md:gap-8 max-w-2xl mx-auto animate-fade-in">
          <div className="text-center group">
            <div className="bg-white/10 backdrop-blur-sm rounded-xl p-3 sm:p-4 mb-2 sm:mb-3 group-hover:bg-white/20 transition-all duration-300">
              <div className="text-xl sm:text-2xl md:text-3xl font-bold text-kawai-sun mb-1">50+</div>
              <div className="text-xs sm:text-sm font-medium">Habitaciones</div>
            </div>
          </div>
          <div className="text-center group">
            <div className="bg-white/10 backdrop-blur-sm rounded-xl p-3 sm:p-4 mb-2 sm:mb-3 group-hover:bg-white/20 transition-all duration-300">
              <div className="text-xl sm:text-2xl md:text-3xl font-bold text-kawai-sun mb-1">100%</div>
              <div className="text-xs sm:text-sm font-medium">Familiar</div>
            </div>
          </div>
          <div className="text-center group">
            <div className="bg-white/10 backdrop-blur-sm rounded-xl p-3 sm:p-4 mb-2 sm:mb-3 group-hover:bg-white/20 transition-all duration-300">
              <div className="text-xl sm:text-2xl md:text-3xl font-bold text-kawai-sun mb-1">24/7</div>
              <div className="text-xs sm:text-sm font-medium">Atención</div>
            </div>
          </div>
        </div>

        {/* Características adicionales */}
        <div className="mt-6 sm:mt-8 animate-slide-up">
          <div className="flex flex-wrap justify-center items-center gap-4 sm:gap-6 text-xs sm:text-sm">
            <div className="flex items-center space-x-2 bg-white/10 backdrop-blur-sm rounded-full px-3 py-1">
              <Shield className="w-3 h-3 text-kawai-sun" />
              <span>Garantía de Calidad</span>
            </div>
            <div className="flex items-center space-x-2 bg-white/10 backdrop-blur-sm rounded-full px-3 py-1">
              <Heart className="w-3 h-3 text-kawai-sun" />
              <span>100% Familiar</span>
            </div>
            <div className="flex items-center space-x-2 bg-white/10 backdrop-blur-sm rounded-full px-3 py-1">
              <Star className="w-3 h-3 text-kawai-sun" />
              <span>Experiencia Premium</span>
            </div>
          </div>
        </div>
      </div>

      {/* Botón scroll mejorado */}
      <button
        onClick={scrollToNext}
        className="absolute bottom-4 sm:bottom-8 left-1/2 transform -translate-x-1/2 z-10 animate-bounce bg-white/10 backdrop-blur-sm rounded-full p-2 sm:p-3 hover:bg-white/20 transition-all duration-300"
        aria-label="Scroll down"
      >
        <ChevronDown size={24} className="text-kawai-sun sm:w-8 sm:h-8" />
      </button>
    </section>
  );
};

export default Hero;
