import React, { useState } from 'react';
import { Phone, Mail, MapPin, Send, CheckCircle, MessageCircle, Shield, Clock, Star, Heart, ChevronDown, Users, Baby } from 'lucide-react';
import { RESORT_INFO, CHECK_IN_OUT } from '../utils/constants';
import { useAppContext } from '../context/AppContext';

const Contacto = () => {
  const { isFormSubmitted, setIsFormSubmitted } = useAppContext();
  const [formData, setFormData] = useState({
    nombre: '',
    email: '',
    telefono: '',
    fechaLlegada: '',
    fechaSalida: '',
    adultos: '2',
    ninos: '0',
    mensaje: ''
  });

  const [errors, setErrors] = useState({});
  const [showAdultos, setShowAdultos] = useState(false);
  const [showNinos, setShowNinos] = useState(false);

  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validatePhone = (phone) => {
    const phoneRegex = /^[\d\s\-\+\(\)]+$/;
    return phoneRegex.test(phone) && phone.replace(/\D/g, '').length >= 7;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Validaciones en tiempo real
    if (name === 'email' && value) {
      if (!validateEmail(value)) {
        setErrors(prev => ({ ...prev, email: 'Ingresa un email válido' }));
      } else {
        setErrors(prev => ({ ...prev, email: '' }));
      }
    }

    if (name === 'telefono' && value) {
      if (!validatePhone(value)) {
        setErrors(prev => ({ ...prev, telefono: 'Ingresa un teléfono válido' }));
      } else {
        setErrors(prev => ({ ...prev, telefono: '' }));
      }
    }

    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validaciones finales
    const newErrors = {};

    if (!validateEmail(formData.email)) {
      newErrors.email = 'Ingresa un email válido';
    }

    if (formData.telefono && !validatePhone(formData.telefono)) {
      newErrors.telefono = 'Ingresa un teléfono válido';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setIsFormSubmitted(true);
    setTimeout(() => {
      setIsFormSubmitted(false);
      setFormData({
        nombre: '',
        email: '',
        telefono: '',
        fechaLlegada: '',
        fechaSalida: '',
        adultos: '2',
        ninos: '0',
        mensaje: ''
      });
      setErrors({});
    }, 3000);
  };

  const infoContacto = [
    {
      icon: <Phone className="w-4 h-4" />,
      titulo: 'Teléfono',
      info: RESORT_INFO.phone,
      color: 'kawai-coral'
    },
    {
      icon: <Mail className="w-4 h-4" />,
      titulo: 'Email',
      info: RESORT_INFO.email,
      color: 'kawai-sun'
    },
    {
      icon: <MapPin className="w-4 h-4" />,
      titulo: 'Ubicación',
      info: RESORT_INFO.address,
      color: 'kawai-palm'
    }
  ];

  const beneficios = [
    {
      icon: <Shield className="w-5 h-5" />,
      titulo: 'Reservas Seguras',
      descripcion: 'Pago seguro y confirmación inmediata',
      color: 'kawai-coral'
    },
    {
      icon: <Clock className="w-5 h-5" />,
      titulo: 'Cancelación Gratuita',
      descripcion: 'Hasta 24 horas antes de tu llegada',
      color: 'kawai-sun'
    },
    {
      icon: <Star className="w-5 h-5" />,
      titulo: 'Mejor Precio',
      descripcion: 'Garantía de precio más bajo',
      color: 'kawai-palm'
    },
    {
      icon: <Heart className="w-5 h-5" />,
      titulo: 'Atención 24/7',
      descripcion: 'Soporte personalizado siempre disponible',
      color: 'kawai-coral'
    }
  ];

  const CustomSelect = ({ value, onChange, options, icon, label, isOpen, setIsOpen, placeholder }) => (
    <div className="relative">
      <label className="block text-sm font-bold text-kawai-text mb-2">
        {label}
      </label>
      <div 
        className="w-full h-12 px-3 border border-gray-300 rounded-xl bg-white/80 backdrop-blur-sm transition-all duration-300 cursor-pointer hover:border-kawai-coral focus-within:ring-2 focus-within:ring-kawai-coral focus-within:border-transparent"
        onClick={() => setIsOpen(!isOpen)}
      >
        <div className="flex items-center justify-between h-full">
          <div className="flex items-center space-x-2">
            <div className="text-kawai-coral">
              {icon}
            </div>
            <span className="text-kawai-text text-sm">{value} {placeholder}</span>
          </div>
          <ChevronDown className={`w-4 h-4 text-kawai-text transition-transform duration-300 ${isOpen ? 'rotate-180' : ''}`} />
        </div>
      </div>

      {isOpen && (
        <div className="absolute z-10 w-full mt-1 bg-white rounded-xl shadow-lg border border-gray-200 max-h-40 overflow-y-auto">
          {options.map((option) => (
            <div
              key={option}
              className="px-3 py-2 hover:bg-kawai-sand/20 cursor-pointer transition-colors duration-200 flex items-center space-x-2"
              onClick={() => {
                onChange(option.toString());
                setIsOpen(false);
              }}
            >
              <div className="text-kawai-coral">
                {icon}
              </div>
              <span className="text-kawai-text text-sm">{option} {placeholder}</span>
              {value === option.toString() && (
                <div className="ml-auto w-2 h-2 bg-kawai-coral rounded-full"></div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );

  return (
    <section id="contacto" className="section-padding bg-gradient-to-br from-kawai-sand/40 to-kawai-warm/30">
      <div className="container-custom">
        <div className="text-center mb-12">
          <h2 className="text-3xl md:text-4xl lg:text-5xl font-lato font-black text-kawai-deep mb-4">
            Contáctanos
          </h2>
          <p className="text-lg text-kawai-text max-w-2xl mx-auto font-light">
            Reserva tu estadía perfecta o consulta cualquier duda que tengas
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          {/* Información de contacto y beneficios */}
          <div className="space-y-6">
            {/* Información de contacto en una fila */}
            <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-6 shadow-lg">
              <h3 className="text-xl font-lato font-black text-kawai-deep mb-4">
                Información de Contacto
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {infoContacto.map((item, index) => (
                  <div key={index} className="flex items-center space-x-3 p-4 rounded-xl bg-gray-50/50">
                    <div className={`bg-${item.color}/10 w-10 h-10 rounded-lg flex items-center justify-center text-${item.color}`}>
                      {item.icon}
                    </div>
                    <div className="flex-1 min-w-0">
                      <h4 className="font-bold text-kawai-deep text-sm">{item.titulo}</h4>
                      <p className="text-kawai-text text-sm truncate">{item.info}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Beneficios simplificados */}
            <div className="bg-gradient-to-br from-kawai-sky/30 to-kawai-coral/20 backdrop-blur-sm rounded-2xl p-8 shadow-lg">
              <h4 className="text-xl font-lato font-black text-kawai-deep mb-6 text-center">
                ¿Por qué elegir Kawai?
              </h4>
              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                {beneficios.map((beneficio, index) => (
                  <div key={index} className="bg-white/60 backdrop-blur-sm rounded-xl p-4">
                    <div className={`bg-${beneficio.color}/10 w-12 h-12 rounded-lg flex items-center justify-center text-${beneficio.color} mb-3`}>
                      {beneficio.icon}
                    </div>
                    <h5 className="font-bold text-kawai-deep text-sm mb-2">
                      {beneficio.titulo}
                    </h5>
                    <p className="text-kawai-text text-sm font-light">
                      {beneficio.descripcion}
                    </p>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Formulario de reserva */}
          <div className="bg-white/80 backdrop-blur-sm rounded-2xl p-8 shadow-lg">
            <div className="flex items-center mb-6">
              <div className="w-12 h-12 bg-kawai-coral/20 rounded-xl flex items-center justify-center mr-4">
                <MessageCircle className="w-6 h-6 text-kawai-coral" />
              </div>
              <div>
                <h3 className="text-2xl font-lato font-black text-kawai-deep">
                  Reserva tu Espacio
                </h3>
                <p className="text-kawai-text text-sm">Completa el formulario y nos pondremos en contacto contigo</p>
              </div>
            </div>

            {isFormSubmitted ? (
              <div className="text-center py-12">
                <div className="w-20 h-20 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-6">
                  <CheckCircle className="w-10 h-10 text-green-500" />
                </div>
                <h4 className="text-xl font-bold text-green-600 mb-3">
                  ¡Mensaje Enviado!
                </h4>
                <p className="text-kawai-text font-light">
                  Nos pondremos en contacto contigo pronto.
                </p>
                <div className="mt-6 text-sm text-kawai-text">
                  <p>✅ Confirmación enviada a tu email</p>
                  <p>⏰ Respuesta en menos de 2 horas</p>
                </div>
              </div>
            ) : (
              <form onSubmit={handleSubmit} className="space-y-6">
                {/* Información personal */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-bold text-kawai-text mb-2">
                      Nombre Completo *
                    </label>
                    <input
                      type="text"
                      name="nombre"
                      value={formData.nombre}
                      onChange={handleChange}
                      required
                      className="w-full h-12 px-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-kawai-coral focus:border-transparent bg-white/80 backdrop-blur-sm transition-all duration-300 text-kawai-text placeholder-gray-400"
                      placeholder="Tu nombre completo"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-bold text-kawai-text mb-2">
                      Email *
                    </label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      className={`w-full h-12 px-4 border rounded-xl focus:ring-2 focus:ring-kawai-coral focus:border-transparent bg-white/80 backdrop-blur-sm transition-all duration-300 text-kawai-text placeholder-gray-400 ${
                        errors.email ? 'border-red-500' : 'border-gray-300'
                      }`}
                      placeholder="tu@email.com"
                    />
                    {errors.email && (
                      <p className="text-red-500 text-xs mt-1">{errors.email}</p>
                    )}
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-bold text-kawai-text mb-2">
                      Teléfono
                    </label>
                    <input
                      type="tel"
                      name="telefono"
                      value={formData.telefono}
                      onChange={handleChange}
                      className={`w-full h-12 px-4 border rounded-xl focus:ring-2 focus:ring-kawai-coral focus:border-transparent bg-white/80 backdrop-blur-sm transition-all duration-300 text-kawai-text placeholder-gray-400 ${
                        errors.telefono ? 'border-red-500' : 'border-gray-300'
                      }`}
                      placeholder="+51 999 999 999"
                    />
                    {errors.telefono && (
                      <p className="text-red-500 text-xs mt-1">{errors.telefono}</p>
                    )}
                  </div>

                  <div>
                    <label className="block text-sm font-bold text-kawai-text mb-2">
                      Fecha de Llegada *
                    </label>
                    <input
                      type="date"
                      name="fechaLlegada"
                      value={formData.fechaLlegada}
                      onChange={handleChange}
                      required
                      className="w-full h-12 px-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-kawai-coral focus:border-transparent bg-white/80 backdrop-blur-sm transition-all duration-300 text-kawai-text"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label className="block text-sm font-bold text-kawai-text mb-2">
                      Fecha de Salida *
                    </label>
                    <input
                      type="date"
                      name="fechaSalida"
                      value={formData.fechaSalida}
                      onChange={handleChange}
                      required
                      className="w-full h-12 px-4 border border-gray-300 rounded-xl focus:ring-2 focus:ring-kawai-coral focus:border-transparent bg-white/80 backdrop-blur-sm transition-all duration-300 text-kawai-text"
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <CustomSelect
                      value={formData.adultos}
                      onChange={(value) => setFormData(prev => ({ ...prev, adultos: value }))}
                      options={[1,2,3,4,5,6]}
                      icon={<Users className="w-4 h-4" />}
                      label="Adultos"
                      isOpen={showAdultos}
                      setIsOpen={setShowAdultos}
                      placeholder="adultos"
                    />

                    <CustomSelect
                      value={formData.ninos}
                      onChange={(value) => setFormData(prev => ({ ...prev, ninos: value }))}
                      options={[0,1,2,3,4,5]}
                      icon={<Baby className="w-4 h-4" />}
                      label="Niños"
                      isOpen={showNinos}
                      setIsOpen={setShowNinos}
                      placeholder="niños"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-bold text-kawai-text mb-2">
                    Mensaje
                  </label>
                  <textarea
                    name="mensaje"
                    value={formData.mensaje}
                    onChange={handleChange}
                    rows="4"
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-kawai-coral focus:border-transparent bg-white/80 backdrop-blur-sm transition-all duration-300 resize-none text-kawai-text placeholder-gray-400"
                    placeholder="Cuéntanos sobre tus preferencias o necesidades especiales..."
                  ></textarea>
                </div>

                <button
                  type="submit"
                  className="w-full bg-gradient-to-r from-kawai-coral to-kawai-sun text-white py-4 px-6 rounded-xl font-semibold transition-all duration-300 transform hover:scale-105 hover:shadow-lg flex items-center justify-center space-x-2"
                >
                  <Send className="w-5 h-5" />
                  <span>Enviar Reserva</span>
                </button>
              </form>
            )}
          </div>
