import React from 'react';
import { Wifi, Utensils, Car, Waves, WifiOff, Heart } from 'lucide-react';
import { services, stats } from '../utils/servicesData';
import { Section } from '../layout';

const Servicios = () => {
  const serviceIcons = {
    wifi: Wifi,
    utensils: Utensils,
    car: Car,
    waves: Waves,
    'wifi-off': WifiOff,
    heart: Heart
  };

  return (
    <Section id="servicios" background="kawai-sand">
      <div className="text-center mb-16">
        <h2 className="text-3xl md:text-4xl lg:text-5xl font-serif font-bold text-kawai-blue mb-4">
          Nuestros Servicios
        </h2>
        <p className="text-lg text-gray-600 max-w-2xl mx-auto">
          Disfruta de todas las comodidades que necesitas para una estadía perfecta con tu familia
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {services.map((servicio, index) => {
          const IconComponent = serviceIcons[servicio.icon];
          return (
            <div
              key={index}
              className="bg-white rounded-xl p-6 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2 group"
            >
              <div className="bg-kawai-warm/10 w-16 h-16 rounded-lg flex items-center justify-center mb-4 group-hover:bg-kawai-warm group-hover:text-white transition-all duration-300">
                <IconComponent className="w-8 h-8" />
              </div>
              <h3 className="text-xl font-semibold text-kawai-blue mb-3">
                {servicio.titulo}
              </h3>
              <p className="text-gray-600 leading-relaxed">
                {servicio.descripcion}
              </p>
            </div>
          );
        })}
      </div>

      <div className="mt-16 text-center">
        <div className="bg-white rounded-2xl p-8 shadow-lg max-w-4xl mx-auto">
          <h3 className="text-2xl font-serif font-bold text-kawai-blue mb-4">
            ¿Por qué elegir Kawai?
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-8">
            {stats.map((stat, index) => (
              <div key={index} className="text-center">
                <div className="text-3xl font-bold text-kawai-warm mb-2">{stat.number}</div>
                <div className="text-gray-600">{stat.label}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </Section>
  );
};

export default Servicios;
