import React from 'react';
import { X, ChevronLeft, ChevronRight } from 'lucide-react';
import { galleryImages } from '../utils/galleryData';
import { useAppContext } from '../context/AppContext';
import { Section } from '../layout';

const Galeria = () => {
  const { selectedImage, setSelectedImage } = useAppContext();

  const openModal = (image) => {
    setSelectedImage(image);
  };

  const closeModal = () => {
    setSelectedImage(null);
  };

  const nextImage = () => {
    if (selectedImage) {
      const currentIndex = galleryImages.findIndex(img => img.id === selectedImage.id);
      const nextIndex = (currentIndex + 1) % galleryImages.length;
      setSelectedImage(galleryImages[nextIndex]);
    }
  };

  const prevImage = () => {
    if (selectedImage) {
      const currentIndex = galleryImages.findIndex(img => img.id === selectedImage.id);
      const prevIndex = currentIndex === 0 ? galleryImages.length - 1 : currentIndex - 1;
      setSelectedImage(galleryImages[prevIndex]);
    }
  };

  return (
    <Section id="galeria" background="white">
      <div className="text-center mb-16">
        <h2 className="text-3xl md:text-4xl lg:text-5xl font-serif font-bold text-kawai-blue mb-4">
          Galer√≠a de Im√°genes
        </h2>
        <p className="text-lg text-gray-600 max-w-2xl mx-auto">
          Descubre la belleza y elegancia de nuestro resort a trav√©s de estas im√°genes
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {galleryImages.map((imagen) => (
          <div
            key={imagen.id}
            className="group relative overflow-hidden rounded-xl cursor-pointer transform hover:scale-105 transition-all duration-300"
            onClick={() => openModal(imagen)}
          >
            <div className="aspect-square bg-gradient-to-br from-kawai-sand to-kawai-warm/20 flex items-center justify-center">
              <div className="text-center p-4">
                <div className="text-4xl mb-2">üèñÔ∏è</div>
                <h3 className="font-semibold text-kawai-blue">{imagen.titulo}</h3>
                <p className="text-sm text-gray-600 mt-2">{imagen.descripcion}</p>
              </div>
            </div>
            <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center">
              <div className="text-white text-center">
                <div className="text-2xl mb-2">üëÅÔ∏è</div>
                <p className="font-semibold">Ver Imagen</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-12 text-center">
        <button className="btn-primary">
          Ver M√°s Im√°genes
        </button>
      </div>

      {selectedImage && (
        <div className="fixed inset-0 bg-black/90 z-50 flex items-center justify-center p-4">
          <div className="relative max-w-4xl w-full">
            <button
              onClick={closeModal}
              className="absolute top-4 right-4 text-white hover:text-gray-300 z-10"
            >
              <X size={32} />
            </button>

            <button
              onClick={prevImage}
              className="absolute left-4 top-1/2 transform -translate-y-1/2 text-white hover:text-gray-300 z-10"
            >
              <ChevronLeft size={32} />
            </button>

            <button
              onClick={nextImage}
              className="absolute right-4 top-1/2 transform -translate-y-1/2 text-white hover:text-gray-300 z-10"
            >
              <ChevronRight size={32} />
            </button>

            <div className="bg-white rounded-xl p-6">
              <div className="aspect-video bg-gradient-to-br from-kawai-sand to-kawai-warm/20 rounded-lg flex items-center justify-center mb-4">
                <div className="text-center">
                  <div className="text-6xl mb-4">üèñÔ∏è</div>
                  <h3 className="text-2xl font-bold text-kawai-blue mb-2">{selectedImage.titulo}</h3>
                  <p className="text-gray-600">{selectedImage.descripcion}</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </Section>
  );
};

export default Galeria;
